let btn=document.getElementById("toggle-sidebar"),aside=document.getElementById("sidebar"),asideH=document.getElementById("content-area");btn.onclick=function(){aside.classList.toggle("no-sidebar"),asideH.classList.toggle("no-sidebar")};let toggle=document.getElementById("toggle-submenue-icon"),toggleMisc=document.getElementById("toggle-misc");toggle.onclick=function(){toggle.classList.toggle("fa-angle-down")},toggleMisc.onclick=(()=>{toggleMisc.classList.toggle("fa-angle-down")});let fullScreen=document.getElementById("toggle-fullscreen");fullScreen.onclick=(()=>{fullScreen.classList.toggle("full-screen"),fullScreen.toString("full-screen")?openFullscreen():closeFullscreen()});let elem=document.documentElement;function openFullscreen(){elem.requestFullscreen?elem.requestFullscreen():elem.webkitRequestFullscreen?elem.webkitRequestFullscreen():elem.msRequestFullscreen&&elem.msRequestFullscreen()}function closeFullscreen(){document.exitFullscreen?document.exitFullscreen:document.webkitExitFullscreen?document.webkitExitFullscreen():document.msExitFullscreen&&document.msExitFullscreen()}